			global::Havit.Diagnostics.Contracts.Contract.Requires(queryParams != null, "queryParams != null");
			
			return ResourceClass.GetFirst(queryParams, null);
		}
		
		/// <summary>
		/// Vrátí první nalezený objekt typu ResourceClass dle parametrů v queryParams.
		/// Pokud není žádný objekt nalezen, vrací null. Data jsou načítána v předané transakci.
		/// </summary>
		public static ResourceClass GetFirst(QueryParams queryParams, DbTransaction transaction)
		{
			global::Havit.Diagnostics.Contracts.Contract.Requires(queryParams != null, "queryParams != null");
			
			int? originalTopRecords = queryParams.TopRecords;
			queryParams.TopRecords = 1;
			ResourceClassCollection getListResult = ResourceClass.GetList(queryParams, transaction);
			queryParams.TopRecords = originalTopRecords;
			return (getListResult.Count == 0) ? null : getListResult[0];
		}
		
		/// <summary>
		/// Vrátí objekty typu ResourceClass dle parametrů v queryParams.
		/// </summary>
		public static ResourceClassCollection GetList(QueryParams queryParams)
		{
			global::Havit.Diagnostics.Contracts.Contract.Requires(queryParams != null, "queryParams != null");
			
			return ResourceClass.GetList(queryParams, null);
		}
		
		/// <summary>
		/// Vrátí objekty typu ResourceClass dle parametrů v queryParams. Data jsou načítána v předané transakci.
		/// </summary>
		public static ResourceClassCollection GetList(QueryParams queryParams, DbTransaction transaction)
		{
			global::Havit.Diagnostics.Contracts.Contract.Requires(queryParams != null, "queryParams != null");
			
			DbCommand dbCommand = DbConnector.Default.ProviderFactory.CreateCommand();
			dbCommand.Transaction = transaction;
			
			queryParams.ObjectInfo = ResourceClass.ObjectInfo;
			if (queryParams.Properties.Count > 0)
			{
				queryParams.Properties.Add(ResourceClass.Properties.ID);
			}
			
			queryParams.PrepareCommand(dbCommand, SqlServerPlatform.SqlServer2008, CommandBuilderOptions.None);
			return ResourceClass.GetList(dbCommand, queryParams.GetDataLoadPower());
		}
		
		private static ResourceClassCollection GetList(DbCommand dbCommand, DataLoadPower dataLoadPower)
		{
			if (dbCommand == null)
			{
				throw new ArgumentNullException("dbCommand");
			}
			
			ResourceClassCollection result = new ResourceClassCollection();
			
			using (DbDataReader reader = DbConnector.Default.ExecuteReader(dbCommand))
			{
				while (reader.Read())
				{
					DataRecord dataRecord = new DataRecord(reader, dataLoadPower);
					ResourceClass resourceClass = ResourceClass.GetObject(dataRecord);
					if (dataLoadPower == DataLoadPower.FullLoad)
					{
						resourceClass.AddDataRecordToCache(dataRecord);
					}
					result.Add(resourceClass);
				}
			}
			
			return result;
		}
		
		private static object lockGetAllCacheAccess = new object();
		
		/// <summary>
		/// Vrátí všechny (příznakem) nesmazané objekty typu ResourceClass.
		/// </summary>
		public static ResourceClassCollection GetAll()
		{
			return ResourceClass.GetAll(false);
		}
		
		/// <summary>
		/// Vrátí všechny objekty typu ResourceClass. Parametr udává, zda se mají vrátit i (příznakem) smazané záznamy.
		/// </summary>
		public static ResourceClassCollection GetAll(bool includeDeleted)
		{
			ResourceClassCollection collection = null;
			int[] ids = null;
			string cacheKey = "Havit.BusinessLayerTest.Resources.ResourceClass.GetAll|includeDeleted=" + includeDeleted;
			
			ids = (int[])HttpRuntime.Cache.Get(cacheKey);
			if (ids == null)
			{
				lock (lockGetAllCacheAccess)
				{
					ids = (int[])HttpRuntime.Cache.Get(cacheKey);
					if (ids == null)
					{
						QueryParams queryParams = new QueryParams();
						queryParams.IncludeDeleted = includeDeleted;
						collection = ResourceClass.GetList(queryParams);
						ids = collection.GetIDs();
						
						HttpRuntime.Cache.Insert(
							cacheKey,
							ids,
							new CacheDependency(null, new string[] { Havit.BusinessLayerTest.Resources.ResourceClass.GetAnySaveCacheDependencyKey() }), 
							Cache.NoAbsoluteExpiration,
							Cache.NoSlidingExpiration,
							CacheItemPriority.Default,
							null); // callback
					}
				}
			}
			if (collection == null)
			{
				collection = new ResourceClassCollection();
				collection.AddRange(ResourceClass.GetObjects(ids));
				collection.LoadAll();
			}
			
			return collection;
		}
		
